const Skills = ({ skill }: any) => {
  const { isDark } = useTheme();
  return (
    <section
      id="skill"
      className="app-container py-10  flex flex-col gap-5 justify-center"
    >
      <div className="font-medium text-3xl underline text-center ">
        {skill.title}
      </div>
      <div className="text-center">{skill.intro}</div>
      <div className="flex gap-5 justify-center flex-wrap">
        {skill?.devops?.map((s: string, i: number) => (
          <Card
            key={i}
            className={`flex flex-col items-center gap-3 p-8
      lg:hover:-translate-y-4 lg:transition-all lg:duration-300
      cursor-pointer hover:rotate-360
      ${isDark ? "bg-white" : "bg-background border shadow-xl"}
    `}
          >
            {icons?.[s]}
            <span className="text-sm font-medium text-muted-foreground">
              {skillLabels[s]}
            </span>
          </Card>
        ))}
      </div>

      <div className="flex justify-center">
        <Card className="p-6 space-y-4 w-fit ">
          {skill?.concepts?.map((section: any) => (
            <div key={section.title} className="space-y-2">
              <h3 className="text-lg font-semibold">{section.title}</h3>

              {section.items.map((item: any) => (
                <p key={item.label} className="text-muted-foreground">
                  <span className="font-medium text-foreground">
                    {item.label}
                  </span>
                  : {item.value}
                </p>
              ))}
            </div>
          ))}
        </Card>
      </div>
      <div className="flex flex-col gap-4">
        <span className="text-center font-medium text-xl underline">
          {skill.others}
        </span>
        <div className="flex gap-5 justify-center flex-wrap ">
          {skill?.fe?.map((s: string, i: number) => (
            <Card
              key={i}
              className={`flex flex-col items-center gap-3 p-8
      lg:hover:-translate-y-4 lg:transition-all lg:duration-300
      cursor-pointer hover:rotate-360
      ${isDark ? "bg-white" : "bg-background border shadow-xl"}
    `}
            >
              {icons?.[s]}
              <span className="text-sm font-medium text-muted-foreground">
                {skillLabels[s]}
              </span>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
